            uri,
            metadata: metadataJSON,
            integrityCheckResult: uriInfo.sha256hash ? uriInfo.sha256hash === sha256Hash : undefined,
            sha256Hash: uriInfo.sha256hash ? sha256Hash : undefined
        }
    }

